mapper.select:
select

from tb_locations


    and local_code = #{localCode,jdbcType=VARCHAR}


    and local_name = #{localName,jdbcType=VARCHAR}


    and lv = #{lv,jdbcType=INTEGER}


    and sup_local_code = #{supLocalCode,jdbcType=VARCHAR}

select

from tb_locations


    and local_code = #{localCode,jdbcType=VARCHAR}


    and local_name = #{localName,jdbcType=VARCHAR}


    and lv = #{lv}


    and sup_local_code = #{supLocalCode,jdbcType=VARCHAR}

select
COUNT(id)
from tb_locations


    and local_code = #{localCode,jdbcType=VARCHAR}


    and local_name = #{localName,jdbcType=VARCHAR}


    and lv = #{lv}


    and sup_local_code = #{supLocalCode,jdbcType=VARCHAR}
