mapper.select:
select distinct u.user_id, u.dept_id, u.login_name, u.user_name, u.user_type, u.email, u.avatar, u.phonenumber, u.status, u.create_time from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.login_name like concat('%', #{loginName}, '%')
AND u.phonenumber like concat('%', #{phoneNumber}, '%')

select
  user_id, dept_id, login_name, user_name, user_type, email, phonenumber, sex, avatar, password, salt, status, del_flag, login_ip, login_date, create_by, create_time, update_by, update_time, remark
from sys_user


        and user_id = #{id)


        and dept_id = #{deptId}


        and login_name = #{loginName}


        and user_name = #{userName}


        and user_type = #{userType}


        and email = #{email}


        and phonenumber = #{phoneNumber}


        and sex = #{sex}


        and avatar = #{avatar}


        and password = #{password}


        and salt = #{salt}


        and status = #{status}


        and del_flag = #{delFlag}


        and login_ip = #{loginIp}


        and login_date = #{loginDate}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and remark = #{remark}

mapper.update:
update sys_user
set
  login_ip = #{loginIp,jdbcType=VARCHAR},
  login_date = #{loginDate,jdbcType=TIMESTAMP}
where user_id = #{id,jdbcType=BIGINT}
